mapper.select:
select * from template where 1=1 

and product_type=#{productType}


and product_name like CONCAT('%',#{productName},'%')


and sku like CONCAT('%',#{sku},'%')


and create_time >= #{startDate} and create_time <= #{endDate}

 order by create_time desc  LIMIT #{beginIndex},#{pageSize}

select count(id) from template where 1=1 

and product_type=#{productType}


and product_name CONCAT('%',#{productName},'%')


and create_time >= #{startDate} and create_time <= #{endDate}
