mapper.select:
select * from template where 1=1 

and product_type=#{productType}


and product_name=#{productName}


and create_time > #{startDate} and create_time < #{endDate}

 order by create_time desc limit 0,10

mapper.insert:
insert into template (id, product_type, sku, 
  product_name, create_time, temp_imgs
  )
values (#{id,jdbcType=INTEGER}, #{productType,jdbcType=VARCHAR}, #{sku,jdbcType=VARCHAR}, 
  #{productName,jdbcType=VARCHAR}, #{createTime,jdbcType=DATE}, #{tempImgs,jdbcType=LONGVARCHAR}
  )
