mapper.select:
select t.*,c.course_name as courseName,su.name as teaName from time_table t left join course c on t.course_id=c.id left join sys_user su on t.tea_id=su.id where t.del_flag=0 
 and (su.username like CONCAT('%',#{search},'%') or c.course_name like CONCAT('%',#{search},'%') or so.name like CONCAT('%',#{search},'%') ) 
 order by day asc,sort asc LIMIT #{beginIndex},#{pageSize}

select t.*,c.course_name as courseName ,so.name as officename from de_course t left join course c on t.course_id=c.id LEFT JOIN sys_office so on so.id=t.office_id where 1=1
and ( c.course_name like CONCAT('%',#{search},'%') or so.name like CONCAT('%',#{search},'%') )
LIMIT #{beginIndex},#{pageSize}

select dc.course_id as courseId,c.course_name from de_course dc left join course c on dc.course_id=c.id where  office_id=#{officeId} 

and  dc.term=#{term}


and dc.teaId=#{teadId}
